import React, { useState, useEffect, useRef } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../App.css';

const Chatbot = () => {
  const [messages, setMessages] = useState([
    { from: "bot", text: "Hello! How can I assist you today?" }
  ]);

  const [input, setInput] = useState("");
  const [isBotTyping, setIsBotTyping] = useState(false);
  const [showPopup, setShowPopup] = useState(false);

  const messagesEndRef = useRef(null);

  // Popup fields (filled from backend)
  const [to, setTo] = useState("");
  const [subject, setSubject] = useState("");
  const [emailBody, setEmailBody] = useState("");

  const [isEditingFields, setIsEditingFields] = useState(false);
  const [isEditingSummary, setIsEditingSummary] = useState(false);

  // ---------------------------
  // SEND MESSAGE (ASK API)
  // ---------------------------
  const handleSend = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    const userMsg = { from: "user", text: input };
    setMessages(prev => [...prev, userMsg]);
    setIsBotTyping(true);

    const query = input;
    setInput("");

    try {
      const response = await fetch("http://localhost:8000/ask", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ query })
      });

      const data = await response.json();

      setMessages(prev => [...prev, { from: "bot", text: data.answer }]);
    } catch (error) {
      setMessages(prev => [...prev, {
        from: "bot",
        text: "Error: Unable to reach the server."
      }]);
    } finally {
      setIsBotTyping(false);
    }
  };

  // ---------------------------
  // PROCESS SUMMARY (EMAIL API)
  // ---------------------------
  const handleSummary = async () => {
    try {
      const response = await fetch("http://localhost:8081/process_and_email", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ messages: messages.map(m => m.text) })
      });

      const data = await response.json();

      // email string looks like:
      // "Subject: XYZ\nTo: abc@company.com\n\nDear Team...\n"
      const emailText = data.email;

      // Extract subject
      const subjectMatch = emailText.match(/Subject:\s*(.*)/);
      const toMatch = emailText.match(/To:\s*(.*)/);

      setSubject(subjectMatch ? subjectMatch[1].trim() : "");
      setTo(toMatch ? toMatch[1].trim() : "");

      // Body = everything after first blank line
      const bodySplit = emailText.split("\n\n");
      setEmailBody(bodySplit.slice(1).join("\n\n"));

      setShowPopup(true);

    } catch (err) {
      alert("Error fetching summary");
    }
  };

  const closePopup = () => {
    setShowPopup(false);
    setIsEditingFields(false);
    setIsEditingSummary(false);
  };

  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);

  return (
    <>
      <div className="main-container">
        <div className="row h-100 g-0">
          
          {/* LEFT SIDEBAR */}
          <div className="col-lg-3 col-md-4 col-12 sidebar">
            <h3>How Argano Can Help You</h3>
            <p className="sidebar-text">
              Argano empowers businesses through digital transformation.
            </p>
            <a href="https://argano.com/" target="_blank" rel="noopener noreferrer"
              className="argano-btn">
              Visit Argano
            </a>
          </div>

          {/* CHAT AREA */}
          <div className="col-lg-9 col-md-8 col-12 chat-container">
            
            {/* MESSAGES */}
            <div className="messages-container">
              {messages.map((msg, index) => (
                <div key={index} className={msg.from === "bot" ? "bot-msg" : "user-msg"}>
                  {msg.text}
                </div>
              ))}

              {isBotTyping && (
                <div className="typing-indicator">
                  <div className="typing-dots">
                    <span></span><span></span><span></span>
                  </div>
                </div>
              )}
              <div ref={messagesEndRef} />
            </div>

            {/* INPUT */}
            <div className="input-container">
              <div className="row g-2">
                <div className="col">
                  <input
                    type="text"
                    className="form-control"
                    placeholder="Type a message..."
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyPress={(e) => e.key === "Enter" && handleSend(e)}
                  />
                </div>

                <div className="col-auto">
                  <button className="btn btn-primary" onClick={handleSend}>Send</button>
                </div>

                <div className="col-auto">
                  <button className="btn btn-secondary" onClick={handleSummary}>Summarize</button>
                </div>
              </div>
            </div>

          </div>
        </div>
      </div>

      {/* POPUP */}
      {showPopup && (
        <div className="modal fade show d-block"
          style={{ backgroundColor: "rgba(0,0,0,0.5)" }}
          onClick={closePopup}>

          <div className="modal-dialog modal-fullscreen" onClick={(e) => e.stopPropagation()}>
            <div className="modal-content">

              <div className="modal-header">
                <h5>Email Summary</h5>
                <button className="btn-close" onClick={closePopup}></button>
              </div>

              <div className="modal-body">

                {/* Email Fields */}
                <div className="email-fields mb-4">
                  <h6>Email Details</h6>

                  <div className="row g-3 mt-2">
                    <div className="col-md-4">
                      <label>To</label>
                      <input type="text" className="form-control"
                        value={to} onChange={e => setTo(e.target.value)} />
                    </div>

                    <div className="col-md-4">
                      <label>Subject</label>
                      <input type="text" className="form-control"
                        value={subject} onChange={e => setSubject(e.target.value)} />
                    </div>
                  </div>
                </div>

                {/* Email Body */}
                <div>
                  <h6>Email Content</h6>
                  <textarea
                    className="form-control"
                    rows="12"
                    value={emailBody}
                    onChange={(e) => setEmailBody(e.target.value)}
                  ></textarea>
                </div>

              </div>

              <div className="modal-footer">
                <button className="btn btn-secondary" onClick={closePopup}>Close</button>
                <button className="btn btn-primary">Send Email</button>
              </div>

            </div>
          </div>

        </div>
      )}
    </>
  );
};

export default Chatbot;
